--[[this meant to be executed through a loadstring]]f=function(t,v)for _,x in ipairs(t)do if x==v then return true end end return false end d=game.Players.LocalPlayer.Data;b={};m={"Wood","Titanium","Rusted","Plastic","Obsidian","Metal","Marble","Ice","Gold","Grass","Glass","Fabric","Concrete","Cane","Brick"}A={"X","Y","Z","XY","XZ","YZ"}for _,x in ipairs(m)do for _,y in ipairs(A)do table.insert(b,x.."Block"..y)end end table.insert(b,"PaintingTool");table.insert(b,"BindTool");table.insert(b,"ScalingTool");table.insert(b,"PropertiesTool");table.insert(b,"TrowelTool");table.insert(b,"Used")if getfenv().debug then local c=os.clock()end for _,x in ipairs(d:GetDescendants())do if x:IsA("IntValue")and not f(b,x.Name)then x.Value=getfenv().desire5;game.Players.LocalPlayer.Character:BreakJoints()end end if getfenv().debug then local d=os.clock()-c;print(d.."s")end
